import React from "react";

import { useState } from "react";
import { motion } from "framer-motion";
import { FaSearch, FaMoon, FaSun } from "react-icons/fa";

export default function App() {
  const [darkMode, setDarkMode] = useState(false);

  return (
    <div className={darkMode ? "bg-gray-900 text-white" : "bg-gray-100 text-gray-900"}>
      {/* Navbar */}
      <nav className="p-4 flex justify-between items-center shadow-md">
        <h1 className="text-2xl font-bold">AI Research Match</h1>
        <div className="flex items-center gap-4">
          <button className="p-2 bg-blue-500 text-white rounded-lg">Sign In</button>
          <button onClick={() => setDarkMode(!darkMode)} className="p-2 rounded-lg">
            {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon className="text-gray-600" />}
          </button>
        </div>
      </nav>

      {/* Hero Section */}
      <header className="text-center py-20 bg-gradient-to-r from-blue-500 to-purple-600 text-white">
        <motion.h2 
          className="text-4xl font-extrabold" 
          initial={{ opacity: 0, y: -20 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1 }}>
          Connect with Researchers Globally
        </motion.h2>
        <p className="mt-4 text-lg">Find the right experts, collaborate, and innovate.</p>
      </header>

      {/* Search Bar */}
      <div className="flex justify-center py-10">
        <div className="flex items-center bg-white shadow-md p-3 rounded-lg w-96">
          <FaSearch className="text-gray-400 mx-2" />
          <input type="text" placeholder="Search for researchers, topics..." className="w-full outline-none" />
        </div>
      </div>

      {/* Researchers List (Sample) */}
      <section className="grid grid-cols-1 md:grid-cols-3 gap-6 p-10">
        {["Dr. Smith", "Dr. Jane", "Prof. Lee"].map((name, index) => (
          <motion.div
            key={index}
            className="bg-white p-5 rounded-lg shadow-lg hover:scale-105 transition-transform"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.2 }}
          >
            <h3 className="text-xl font-semibold">{name}</h3>
            <p className="text-gray-500">Expert in AI & Machine Learning</p>
          </motion.div>
        ))}
      </section>
    </div>
  );
}
