import React, { useState } from "react";
import { OpenAI } from "openai";
import "./Chatbot.css";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const openai = new OpenAI({
    apiKey: process.env.REACT_APP_OPENAI_API_KEY, // Store your API key in .env
    dangerouslyAllowBrowser: true, // Allow browser usage (not recommended for production)
  });

  const handleSend = async () => {
    if (input.trim()) {
      setIsLoading(true);
      setMessages([...messages, { text: input, sender: "user" }]);

      try {
        // Call OpenAI API
        const response = await openai.chat.completions.create({
          model: "gpt-3.5-turbo", // Use GPT-3.5 or GPT-4
          messages: [
            { role: "system", content: "You are a helpful assistant for the ResearchCollab platform." },
            { role: "user", content: input },
          ],
        });

        const botResponse = response.choices[0].message.content;
        setMessages([...messages, { text: input, sender: "user" }, { text: botResponse, sender: "bot" }]);
      } catch (error) {
        console.error("Error fetching response from OpenAI:", error);
        setMessages([...messages, { text: input, sender: "user" }, { text: "Sorry, something went wrong. Please try again.", sender: "bot" }]);
      } finally {
        setIsLoading(false);
        setInput("");
      }
    }
  };

  return (
    <div className="chatbot">
      <div className="chatbot-header">
        <h3>ResearchBot</h3>
        <button onClick={() => setMessages([])}>Clear</button>
      </div>
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            {message.text}
          </div>
        ))}
        {isLoading && <div className="message bot">Typing...</div>}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSend()}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
