import React, { useState } from "react";
import "./Chatbot.css";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const predefinedQuestions = [
    "How do I search for research papers?",
    "Can I collaborate with other researchers?",
    "What is ResearchCollab?",
  ];

  const handleSend = async () => {
    if (input.trim()) {
      setIsLoading(true);
      setMessages([...messages, { text: input, sender: "user" }]);

      try {
        const response = await fetch("http://localhost:5000/chat", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            messages: [
              { role: "system", content: "You are a helpful assistant for the ResearchCollab platform." },
              { role: "user", content: input },
            ],
          }),
        });
        const botResponse = await response.json();
        setMessages([...messages, { text: input, sender: "user" }, { text: botResponse, sender: "bot" }]);
      } catch (error) {
        console.error("Error fetching response:", error);
        setMessages([...messages, { text: input, sender: "user" }, { text: "Sorry, something went wrong. Please try again.", sender: "bot" }]);
      } finally {
        setIsLoading(false);
        setInput("");
      }
    }
  };

  return (
    <div className="chatbot">
      <div className="chatbot-header">
        <h3>ResearchBot</h3>
        <button onClick={() => setMessages([])}>Clear</button>
      </div>
      <div className="chatbot-messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            {message.text}
          </div>
        ))}
        {isLoading && <div className="message bot">Typing...</div>}
      </div>
      <div className="predefined-questions">
        {predefinedQuestions.map((question, index) => (
          <button key={index} onClick={() => setInput(question)}>
            {question}
          </button>
        ))}
      </div>
      <div className="chatbot-input">
        <input
          type="text"
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && handleSend()}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
