import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import "./App.css";

function App() {
  const [isLoginOpen, setLoginOpen] = useState(false);
  const [isSignupOpen, setSignupOpen] = useState(false);
  const [isDarkMode, setDarkMode] = useState(
    localStorage.getItem("theme") === "dark"
  );

  // Apply dark mode settings on page load
  useEffect(() => {
    document.body.classList.toggle("dark-mode", isDarkMode);
    localStorage.setItem("theme", isDarkMode ? "dark" : "light");
  }, [isDarkMode]);

  return (
    <div className={`container ${isDarkMode ? "dark" : ""}`}>
      {/* Navbar */}
      <nav className="navbar">
        <h2>ResearchMatch</h2>
        <ul>
          <li><a href="#home">Home</a></li>
          <li><a href="#features">Features</a></li>
          <li><a href="#about">About</a></li>
          <li onClick={() => setLoginOpen(true)}>Login</li>
          <li onClick={() => setSignupOpen(true)}>Sign Up</li>
          <li onClick={() => setDarkMode(!isDarkMode)}>
            {isDarkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
          </li>
        </ul>
      </nav>

      {/* Hero Section */}
      <motion.header
        className="hero"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h1>AI Research Matchmaking</h1>
        <p>Find researchers, explore datasets, and advance AI-driven research.</p>
        <motion.button className="explore-btn" whileHover={{ scale: 1.1 }}>
          Get Started
        </motion.button>
      </motion.header>

      {/* Search Bar */}
      <div className="search-container">
        <input type="text" placeholder="Search research papers, datasets, experts..." />
        <button>Search</button>
      </div>

      {/* Features Section */}
      <section id="features" className="features">
        <h2>Key Features</h2>
        <div className="feature-grid">
          <motion.div className="feature-card" whileHover={{ scale: 1.05 }}>üîç Smart Research Discovery</motion.div>
          <motion.div className="feature-card" whileHover={{ scale: 1.05 }}>üìú AI-Powered Paper Translation</motion.div>
          <motion.div className="feature-card" whileHover={{ scale: 1.05 }}>üìù Quick AI Research Summaries</motion.div>
          <motion.div className="feature-card" whileHover={{ scale: 1.05 }}>ü§ù AI-Powered Matchmaking</motion.div>
          <motion.div className="feature-card" whileHover={{ scale: 1.05 }}>üìä One-Click Data Exploration</motion.div>
          <motion.div className="feature-card" whileHover={{ scale: 1.05 }}>üìà Paper Impact Tracker</motion.div>
        </div>
      </section>

      {/* Testimonials */}
      <section className="testimonials">
        <h2>What Researchers Say</h2>
        <p>‚ÄúThis platform changed the way I collaborate with AI experts!‚Äù</p>
        <p>‚Äî Dr. Alex Johnson</p>
      </section>

      {/* Footer */}
      <footer>
        <p>¬© 2025 ResearchMatch. All rights reserved.</p>
        <div className="social-icons">
          <a href="#">üîó LinkedIn</a>
          <a href="#">üìò Facebook</a>
          <a href="#">üê¶ Twitter</a>
        </div>
      </footer>

      {/* Login Popup */}
      {isLoginOpen && (
        <div className="popup">
          <div className="popup-content">
            <h3>Login</h3>
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button>Login</button>
            <button onClick={() => setLoginOpen(false)}>Close</button>
          </div>
        </div>
      )}

      {/* Signup Popup */}
      {isSignupOpen && (
        <div className="popup">
          <div className="popup-content">
            <h3>Sign Up</h3>
            <input type="text" placeholder="Full Name" />
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button>Sign Up</button>
            <button onClick={() => setSignupOpen(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
